class Model_Core_Setting extends Abstract_Core_Model {

    protected $_table_name = 'core_setting';
    protected $_pk_field = 'record_id';
    protected $_ukey_field = 'setting_key';
    protected $_allow_write_log = true;

    public static function validateSettingKey($key) {
        if (preg_match('/[^a-zA-Z0-9\/\_]/', $key) || substr($key, 0, 1) == '/' || substr($key, -1) == '/' || strpos($key, '//') !== false) {
            throw new Exception('Setting key is incorrect format');
        }
    }

    protected function _beforeSave() {
        parent::_beforeSave();

        $data = $this->_collectDataForSave();

        $errors = array();

        if (isset($data['setting_key'])) {
            try {
                static::validateSettingKey($data['setting_key']);
            } catch (Exception $ex) {
                $errors[] = $ex->getMessage();
            }
        }

        foreach (['added_timestamp', 'modified_timestamp'] as $key) {
            if (isset($data[$key])) {
                $data[$key] = intval($data[$key]);

                if ($data[$key] < 0) {
                    $data[$key] = 0;
                }
            }
        }

        if (count($errors) < 1) {
            if ($this->getActionFlag() == static::INSERT_FLAG) {
                $require_fields = [
                    'setting_key' => 'Setting key is empty'
                ];

                foreach ($require_fields as $field_name => $err_message) {
                    if (!isset($data[$field_name])) {
                        $errors[] = $err_message;
                    }
                }

                $default_fields = [
                    'setting_value' => null,
                    'added_timestamp' => time(),
                    'modified_timestamp' => time()
                ];

                foreach ($default_fields as $field_name => $default_value) {
                    if (!isset($data[$field_name])) {
                        $data[$field_name] = $default_value;
                    }
                }
            } else {
                unset($data['setting_key']);
                $data['modified_timestamp'] = time();
            }
        }

        $this->resetDataModifiedMap()->setData($data);

        if (count($errors) > 0) {
            $this->_error($errors);
            return false;
        }
    }

    protected function _preDataForSave(&$data) {
        parent::_preDataForSave($data);

        foreach (['setting_value'] as $key) {
            if (isset($data[$key])) {
                $data[$key] = OSC::encode($data[$key]);
            }
        }
    }

    protected function _preDataForUsing(&$data) {
        parent::_preDataForUsing($data);

        foreach (['setting_value'] as $key) {
            if (isset($data[$key])) {
                $data[$key] = OSC::decode($data[$key], true);
            }
        }
    }

}
